mapper.update:
UPDATE PRODUCTION_PLAN
SET ACTUAL_QTY = (SELECT NVL(SUM(DAILY_QTY), 0) FROM PRODUCTION_DAILY WHERE PLAN_ID = #{planId})
WHERE PLAN_ID = #{planId}

mapper.select:
SELECT PP.PLAN_ID, PP.ITEM_CODE, I.ITEM_NAME, PP.START_DATE, PP.DUE_DATE, PP.COMPLETION_DATE, PP.PLAN_QTY, PP.ACTUAL_QTY, PP.STATUS, PP.REMARK, PP.EMP_NO FROM PRODUCTION_PLAN PP JOIN ITEM I ON PP.ITEM_CODE = I.ITEM_CODE
PP.STATUS = '작업지시'
AND ( PP.PLAN_ID LIKE '%' || #{keyword} || '%' OR I.ITEM_NAME LIKE '%' || #{keyword} || '%' )
AND PP.START_DATE >= #{startDate}
AND PP.DUE_DATE <= #{endDate}
AND PP.EMP_NO = #{empNo}
ORDER BY PP.PLAN_ID DESC
