mapper.select:
SELECT * FROM ( SELECT InnerQuery.*, ROWNUM AS rn FROM ( SELECT PURCHASE_ID, PURCHASE_CODE, ORDER_DATE, SUPPLIER_NAME, TOTAL_AMOUNT, STATUS, EMPLOYEE_NAME, CREATED_AT, REMARK FROM V_PURCHASE_LIST WHERE 1 = 1
AND (SUPPLIER_NAME LIKE '%' || #{keyword} || '%' OR EMPLOYEE_NAME LIKE '%' || #{keyword} || '%')
AND STATUS = #{status}
AND ORDER_DATE >= TO_DATE(#{startDate}, 'YYYY-MM-DD')
AND ORDER_DATE <= TO_DATE(#{endDate}, 'YYYY-MM-DD')
ORDER BY ORDER_DATE DESC, PURCHASE_ID DESC ) InnerQuery WHERE ROWNUM <= #{endRowForQuery} ) WHERE rn > #{startRowForQuery}
