mapper.select:
SELECT * FROM ( SELECT InnerQuery.*, ROWNUM AS rn FROM ( SELECT PURCHASE_ID, PURCHASE_CODE, PURCHASE_DATE AS ORDER_DATE, SUPPLIER_NAME, TOTAL_AMOUNT, STATUS, EMPLOYEE_NAME, CREATED_AT, REMARK FROM V_PURCHASE_LIST WHERE 1 = 1
AND (SUPPLIER_NAME LIKE '%' || #{keyword} || '%' OR EMPLOYEE_NAME LIKE '%' || #{keyword} || '%')
AND STATUS = #{status}
AND PURCHASE_DATE >= TO_DATE(#{startDate}, 'YYYY-MM-DD')
AND PURCHASE_DATE <= TO_DATE(#{endDate}, 'YYYY-MM-DD')
ORDER BY PURCHASE_DATE DESC, PURCHASE_ID DESC ) InnerQuery WHERE ROWNUM <= #{endRowForQuery} ) WHERE rn > #{startRowForQuery}

mapper.insert:
INSERT INTO PURCHASE_HEADER (
